document.addEventListener("DOMContentLoaded",function(){const e=document.querySelector('[data-js="birthdate"]'),a=document.querySelector('[data-js="birthdate-day"]'),n=document.querySelector('[data-js="birthdate-month"]'),d=document.querySelector('[data-js="birthdate-year"]');e.addEventListener("change",()=>{const t=new Date(e.value);isNaN(t.getTime())?(a.value="",n.value="",d.value=""):(a.value=String(t.getDate()).padStart(2,"0"),n.value=String(t.getMonth()+1).padStart(2,"0"),d.value=t.getFullYear())})});
